int main()
{
    ios::sync_with_stdio(false);
    cin >> l >> r >> c;
    int sx, sy, sz;
    int ex, ey, ez;
    for(int i = 1; i <= l; i++)
    {
        for(int j = 1; j <= r; j++)
        {
            for(int k = 1; k <= c; k++)
            {
                cin >> themap[j][k][i];
                if(themap[j][k][i] == 'S')
                {
                    sx = j;
                    sy = k;
                    sz = i;
                }
                if(themap[j][k][i] == 'E')
                {
                    ex = j;
                    ey = k;
                    ez = i;
                }
            }
        }
    }

    bfs(sx, sy, sz);
    if(flag[ex][ey][ez] == 0) cout << "Trapped!" << endl;
    else cout << "Escaped in " << dist[ex][ey][ez] << " minute(s)." << endl;

    return 0;
}
