```cpp
#include <cstdio>
using namespace std ;

long long a[100002], b[100002], n, m, s, e, h, start, end, sum = 0 ;
int main()
{
    scanf("%lld", &n) ;
    for(int i = 1; i <= n; i ++)
    {
        scanf("%lld", &a[i]) ;
    }

    for(int i = 1; i <= n; i ++)
    {
        b[i] = a[i] - a[i - 1] ;
    }

    scanf("%lld", &m) ;
    for(int i = 1; i <= m; i ++)
    {
        scanf("%lld %lld %lld", &s, &e, &h) ;
        b[s] += h ;
        b[e + 1] -= h ;
    }
    for(int i = 1; i <= n; i ++)
    {
        a[i] = a[i - 1] + b[i] ;
    }

    scanf("%lld %lld", &start, &end) ;
    for(int i = start; i <= end; i ++)
    {
        sum += a[i] ;
    }
    printf("%lld\n", sum) ;

    return 0 ;
}
