#include <cstdio>
using namespace std;

int n, m, t;
int sx, sy, fx, fy;
int sum = 0;
bool a[10][10];
bool flag[10][10];

void dg(int x, int y)
{
    if(x == fx && y == fy)
    {
        sum++ ;
        return;
    }
    if(x - 1 >= 1 && a[x - 1][y] != 1 && flag[x - 1][y] == 0)
    {
        flag[x - 1][y] = 1;
        dg(x - 1, y);
        flag[x - 1][y] = 0;
    }
    if(x + 1 <= n && a[x + 1][y] != 1 && flag[x + 1][y] == 0)
    {
        flag[x + 1][y] = 1;
        dg(x + 1, y);
        flag[x + 1][y] = 0;
    }
    if(y - 1 >= 1 && a[x][y - 1] != 1 && flag[x][y - 1] == 0)
    {
        flag[x][y - 1] = 1;
        dg(x, y - 1);
        flag[x][y - 1] = 0;
    }
    if(y + 1 <= m && a[x][y + 1] != 1 && flag[x][y + 1] == 0)
    {
        flag[x][y + 1] = 1;
        dg(x, y + 1);
        flag[x][y + 1] = 0;
    }
}

int main()
{
    scanf("%d %d %d", &n, &m, &t);
    scanf("%d %d %d %d", &sx, &sy, &fx, &fy);
    for(int i = 1; i <= t; i++)
    {
        int zx, zy;
        scanf("%d %d", &zx, &zy);
        a[zx][zy] = 1;
    }

    flag[sx][sy] = 1;
    dg(sx, sy);
    printf("%d\n", sum);

    return 0;
}
```

