```cpp
#include <malloc.h>
#include <cstdio>
using namespace std;

struct node // 
{
    int x; // 
    node *next; // 
};

node *head; // 

void push(int data) // `data` 
{
    if(head == NULL) // 
    {
        node *New = (node *) malloc(sizeof(node)); // 
        (*New).x = data; // 
        (*New).next = NULL; // 
        head = New; // 
    }
    else
    {
        node *s = head; // 
        while((*s).next != NULL) // 
        {
            s = (*s).next; // 
        }
        node *New = (node *) malloc(sizeof(node)); //  `head==NULL`
        (*New).x = data;
        (*New).next = NULL;
        (*s).next = New;
    }
}
void insert(int x, int y) //  `x` `y`  `y` 
{
    node *s = head;
    y-- ;
    while(y)
    {
        s = (*s).next;
        y-- ;
    }
    node *New = (node *) malloc(sizeof(node));
    (*New).x = x;
    (*New).next = (*s).next;
    (*s).next = New;
}
int find(int x) //  `x` 
{
    node *s = head;
    x-- ;
    while(x)
    {
        s = (*s).next;
        x-- ;
    }
    return (*s).x;
}
void update(int x, int y) //  `x`  `y`
{
    node *s = head;
    x-- ;
    while(x)
    {
        s = (*s).next;
        x-- ;
    }
    (*s).x = y;
}
void deletes(int x) //  `x` 
{
    if(x == 1)
    {
        head = (*head).next;
        return;
    }
    node *s = head;
    x-- ;
    x-- ;
    while(x--)
    {
        s = (*s).next;
        x-- ;
    }
    node *t = (*((*s).next)).next; // 
    free((*s).next); // 
    (*s).next = t;
}

int main() // main() 
{
    push(100); //  100
    push(200); //  200
    insert(300, 1); //  300
    printf("first:%d, second:%d, third:%d\n", find(1), find(2), find(3)); //  100 300 200
    deletes(1); // 
    insert(400, 1); //  400
    printf("first:%d, second:%d, third:%d\n", find(1), find(2), find(3)); //  300 400 200
    return 0;
}
```


