#include <cstdio>
using namespace std;

int n, m;
int t;

int dg(int x)
{   
    if(x == m) return 1;
    if(x < m || x % 3 != 0) return 0;


    int ans1 = dg(x / 3);
    int ans2 = dg(x / 3 * 2);
    // printf("ans1:%d, ans2:%d\n", ans1, ans2);

    return ans1 + ans2;
}

int main()
{
    scanf("%d", &t);

    for(int i = 1; i <= t; i++)
    {
        scanf("%d %d", &n, &m);

        int ans = dg(n);
        // printf("ans:%d\n", ans);
        if(ans > 0) printf("YES\n");
        else printf("NO\n");
    }

    return 0;
}
```

