const int TEMP = 5e5 + 3;
int a[TEMP], b[TEMP];
long long sum = 0;

void dg(int l, int r)
{
    int mid = (l + r) / 2;
    if(l != r)
    {
        dg(l, mid);
        dg(mid + 1, r);
    }

    int l1 = l, l2 = mid + 1;
