const int TEMP = 1e8 + 12;
bool vis[TEMP];
int pri[TEMP], cnt = 0;
void is_prime(int n)
{
    for(int i = 2; i < n; ++i)
    {
        if(!vis[i])
        {
            pri[cnt++] = i;
        }
        for(int j = 0; j < cnt; ++j)
        {
            if(i * pri[j] >= n)
            {
                break;
            }
            vis[i * pri[j]] = 1;
            if(i % pri[j] == 0)
            {
