    for(int i = 2; i * i <= n; i++) // 
    {
        if(flag[i] == 0) // 
        {
            for(int j = i * 2; j <= n; j += i) //  i * 2 += i 
            {
                flag[j] = 1;
            }
        }
    }
}

int main()
{
