    scanf("%d", &n);
    is_prime(n);
    FILE *fp = freopen("./ans.txt", "w", stdout); // 
    for(int i = 2; i <= n; i++)
    {
        if(flag[i] == 0) // 
        {
            printf("%d\n", i);
        }
    }
    fclose(fp); // 
}
